		public static string GetFullnameByID(int npcID)
		{
			for (int i = 0; i < 200; i++)
			{
				if (Main.npc[i].active && Main.npc[i].type == npcID)
				{
					return Main.npc[i].FullName;
				}
			}
			return Lang.GetNPCNameValue(npcID);
		}

